import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAddresses } from "../actions";
import { Link } from "react-router-dom";
import { Card, Button } from "reactstrap";
import CardAddress from "./CardAddress";
import "bootstrap/dist/css/bootstrap.min.css";
import { account_balance_wallet, grid_view } from "material-icons";

export default function Home() {
  const dispatch = useDispatch();
  const addresses = useSelector((state) => state.addresses);
  const [address, setAddress] = useState("");

  useEffect(() => {
    dispatch(getAddresses());
  }, [dispatch]);

  const handleClick = (e) => {
    e.preventDefault();
    dispatch(getAddresses());
  };

  return (
    <div className="container py-5">
      <div className="row">
        <div className="col-md-4">
        <h1>
              <span class="material-icons"> grid_view </span>
              My Wallet
            </h1>
            
          <br />
          <Button onClick={handleClick}>Refresh</Button>
          <br />
          <div className="card card-body bg-dark rounded-0 mb-4">
            <br />

            <form id="taskForm" class="card card-body bg-dark">
            <h4>Look for an address</h4>
            <input
              type="text"
              id="title"
              class="form-control bg-dark border-0 rounded-0 text-light my-4"
              placeholder="Enter an address"
              autofocus
            />
            <button class="btn btn-primary" onClick={handleClick}>Search</button>
          </form>
          </div>
          <br />
        </div>
        <div class="d-flex align-items-center">
              <span class="material-icons"> account_balance_wallet </span>
              <span class="ms-2">Wallet Addresses</span>
            </div>
        <Card>
          {addresses?.map((address) => (
            <fragment>
              <br />
              <CardAddress
                key={address.address}
                account={address.account}
                balance={address.balance}
              />
            </fragment>
          ))}
        </Card>
      </div>
    </div>
  );
}
