const initialState = {
  addresses: [],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "GET_ADDRESSES":
      return {
        ...state,
        addresses: action.payload,
      };
    default:
      return state;
  }
}

export default rootReducer;
