import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAddresses } from "../actions";
import { Button } from "reactstrap";
import CardAddress from "./CardAddress";
import SearchBar from "./SearchBar";
import "bootstrap/dist/css/bootstrap.min.css";
import { account_balance_wallet, grid_view } from "material-icons";
import { removeFromFavorites, addToFavorites } from "../actions";
import { AiOutlineHeart, AiFillHeart } from "react-icons/ai";


export default function Home() {

  const dispatch = useDispatch();

  const addresses = useSelector((state) => state.addresses);
  const favorites = useSelector((state) => state.favorites);

  const isFavorite = (account) => {
    return favorites.some((el) => el.account === account);
  };

  useEffect(() => {
    dispatch(getAddresses());
  }, [dispatch]);

  const handleClick = (e) => {
    e.preventDefault();
    dispatch(getAddresses()); 
  };

  const handleFavorites = (e) => {
    e.preventDefault();
    if (favorites.some((el) => el.account === e.target.value)) 
    { 
      dispatch(removeFromFavorites(e.target.value));
    } else {
      dispatch(addToFavorites(e.target.value));
    }
  };

  return (
    <div className="container py-5">
      <div className="row">
        <div className="col-md-4">
          <h1>
            <span className="material-icons"> grid_view </span>
            My Wallet
          </h1>

          <br />
          <Button onClick={handleClick}>Refresh</Button>
          <br />
          <br />

          <SearchBar />

          <br />
          <br />

          <div className="col-md-12">
            <div id="tasksList">
              <div className="d-flex align-items-center">
                <span className="material-icons"> account_balance_wallet </span>
                <h3 className="ms-2">Wallet Addresses</h3>
              </div>
              <div className="card card-body bg-dark rounded-0 mb-8">
                {addresses?.map((address) => (
                  <>
                    <br />
                    <CardAddress
                      key={address.account}
                      account={address.account}
                      balance={address.balance}
                      value={address.account}
                    />
                    <button onClick={(e) => handleFavorites(e)}>
                    {isFavorite() ? (
                        <AiFillHeart />
                    ) : (
                        <AiOutlineHeart />
                    )}
                </button>
                  </>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
