import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { removeFromFavorites, addToFavorites } from "../actions";
import { AiOutlineHeart, AiFillHeart } from "react-icons/ai";


export default function CardAddress({ account, balance, id }) {

    const dispatch = useDispatch();

    const favorites = useSelector(state => state.favorites);

    const addFavourites = (id) => {
        if (favorites.some((el) => el.id === id)) {
          dispatch(removeFromFavorites(id));
        } else {
          dispatch(addToFavorites(id));
        }
      };
      const isFavorite = () => {
        return favorites.some((el) => el.id === id);
      };


    return (
        <div className="card">
            <div className="card-body">
                <h5 className="card-title">Account: {account}</h5>
                <p className="card-text">Balance: {balance}</p>
                <button onClick={addFavourites}>
                    {isFavorite(id) ? (
                        <AiFillHeart />
                    ) : (
                        <AiOutlineHeart />
                    )}
                </button>
            </div>
        </div>
    );
}
